      super(props);
      this.state = {compt: 0};

      // This binding is necessary to make `this` work in the callback
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
      this.setState(state => ({
        compt:  state.compt + 1,
      }));
    }

    render() {
      let {phrase} = this.props
      return (
        <div>
          {this.props}  
          <div>
            <button onClick={this.handleClick}>
              click me
