import React, { Component } from 'react';



class Ex2 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {compt: 0};

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      compt:  state.compt + 1
    }));
  }

  render() {

    let phrase = this.props
    return (
      <div>
        Nombre de click = {this.state.compt} 
        <div>
          <button onClick={this.handleClick}>
            click me
          </button>
        </div>
      </div>
    );
  }
}

export default Ex2;

function pluriel(props) {
  if (this.state.compt >= 2 ) {
    return <h1>Il y a eu {this.state.compt} {props.singulier} </h1>;
  }
  else {
    return <h1>Il y a eu {this.state.compt} {props.pluriel} </h1>;
  }

}
